import React from 'react';
import logo from './logo.svg';
import './App.css';
import AppClock from './components/AppClock';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1 class="app-title">
          Pomodoro Clock
        </h1>
      </header>
      <Pomodoro />
      <footer>
      </footer>
    </div>
  );
}
class Pomodoro extends React.Component {
   constructor(props) {
     super(props);
     this.state = {
       break: 5,
       session: 25,
       active: false,
       timestamp: 0,
     };
   }
   decrementTime = (element, elementName) => {
     if(element > 0) {
       this.setState({
         elementName: ++this.state.elementName
       });
     }
   }
   incrementTime = (element, elementName) => {
     if(element > 0) {
       this.setState({
         elementName: --this.state.elementName
       });
     }
   }
   render(){
     return(
       <main>
         <Break
           break={this.state.break}
           decrementTime={this.decrementTime}
           incrementTime={this.incrementTime}
           />
         <Session
           session={this.state.session}
           decrementTime={this.decrementTime}
           incrementTime={this.incrementTime}
           />
         <Timer/>
       </main>
     );
   }
}
class Break extends React.Component {
   constructor(props) {
     super(props);
     this.state = {
       value: 5,
     };
   }
   render(){
     return(
       <div>
         <label id="break-label">
           <span>"Break Length"</span>
         </label>
         <button id="break-decrement"></button>
         <span id="break-length">{this.state.value}</span>
         <button id="break-increment"></button>
       </div>
     );
   }
}

class Session extends React.Component {
   constructor(props) {
     super(props);
     this.state = {
       value: 25,
     };
   }
   render(){
     return(
       <div>
         <label id="session-label">
           <span>"Session Length"</span>
         </label>
         <button id="session-decrement"></button>
        <span id="session-length">{this.state.value}</span>
         <button id="session-increment"></button>
       </div>
     );
   }
}

class Timer extends React.Component {
   constructor(props) {
     super(props);
     this.state = {
       value: 25,
     };
   }
   render(){
     return(
       <div>
         <label id="timer-label">
           <span>Session</span>
         </label>
         <span id="time-left">{this.state.session}</span>
         <button id="start_stop">Play / Pause</button>
         <button id="reset">Reset</button>
       </div>
     );
   }
}



export default App;
