import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1 className="app-title">
          Pomodoro Clock
        </h1>
      </header>
      <Pomodoro />
      <footer>
      </footer>
    </div>
  );
}
class Pomodoro extends React.Component {
   constructor(props) {
     super(props);
     this.state = {
       break: 5,
       session: 25,
       active: false,
       timestamp: 0,
     };
   }
   decrementTime = (element, elementName) => {
     if(element > 0) {
       this.setState({
         elementName: ++this.state[elementName]
       }, ()=>console.log(this.state[elementName]));
     }
   }
   incrementTime = (element, elementName) => {
     if(element > 0) {
       this.setState({
         elementName: --this.state[elementName]
       }, ()=>console.log(this.state[elementName]));
     }
   }
   render(){
     return(
       <main>
         <Break
           break={this.state.break}
           decrementTime={this.decrementTime}
           incrementTime={this.incrementTime}
           />
         <Session
           session={this.state.session}
           decrementTime={this.decrementTime}
           incrementTime={this.incrementTime}
           />
         <Timer/>
       </main>
     );
   }
}
class Break extends React.Component {
   constructor(props) {
     super(props);
     this.state = {

     };
   }
   handleClick = (e) => {
     console.log(e.target);
     if (e.target.id === 'break-decrement') this.props.decrementTime(this.props.break, 'break');
     if (e.target.id === 'break-increment') this.props.incrementTime(this.props.break, 'break');
   }
   render(){
     return(
       <div>
         <label id="break-label">
           <span>"Break Length"</span>
         </label>
         <button
           id="break-decrement"
           onClick={this.handleClick}
           ></button>
         <span id="break-length">{this.props.break}</span>
         <button
           id="break-increment"
           onClick={this.handleCLick}
           ></button>
       </div>
     );
   }
}

class Session extends React.Component {
   constructor(props) {
     super(props);
     this.state = {

     };
   }
   render(){
     return(
       <div>
         <label id="session-label">
           <span>"Session Length"</span>
         </label>
         <button id="session-decrement"></button>
        <span id="session-length">{this.state.session}</span>
         <button id="session-increment"></button>
       </div>
     );
   }
}

class Timer extends React.Component {
   constructor(props) {
     super(props);
     this.state = {
       value: 25,
     };
   }
   render(){
     return(
       <div>
         <label id="timer-label">
           <span>Session</span>
         </label>
         <span id="time-left">{this.state.session}</span>
         <button id="start_stop">Play / Pause</button>
         <button id="reset">Reset</button>
       </div>
     );
   }
}



export default App;
